describe("Dialog system tests",function(){describe("About dialog",function(){it("Should be opened when use clicks on the About button",function(){getBox().style.width="100%";var e=new Jodit(appendTestArea(),{disablePlugins:"mobile"}),t=e.container.querySelector(".jodit_toolbar_btn.jodit_toolbar_btn-about");expect(t).to.be.not.equal(null),simulateEvent("mousedown",0,t);var o=e.ownerDocument.querySelector(".jodit.jodit_dialog_box.active");expect(o).to.be.not.equal(null),expect(-1!==o.innerHTML.indexOf("xdsoft.net")).to.be.equal(!0)})}),describe("Short Jodit.Alert etc static methods",function(){it("Should work without Jodit instance",function(){Jodit.Alert("Hello").close()}),it("Should return Dialog instance",function(){var e=Jodit.Alert("Hello");expect(e instanceof Jodit.modules.Dialog).to.equal(!0),e.close()}),it("Should get string or HTMLElement or array of string or array of HTMLElement in arguments",function(){var e=Jodit.Alert(['<div id="hello1">Hello</div>']);expect(document.getElementById("hello1")).not.to.equal(null),e.close();var t=Jodit.Alert(document.createTextNode("Test"));expect(t instanceof Jodit.modules.Dialog).to.equal(!0),t.close();var o=document.createElement("div");o.id="hello3";var i=Jodit.Alert(o);expect(o).to.equal(document.getElementById("hello3")),i.close()})}),describe("Dialog image",function(){describe("Opened dialog image",function(){it("Should disable margin inputs for left, bottom, right if element has equals margins(margin:10px;)",function(){var e=new Jodit(appendTestArea(),{observer:{timeout:0},image:{openOnDblClick:!0}});e.setEditorValue('<img src="https://xdsoft.net/jodit/build/images/artio.jpg" style="margin:10px;border:1px solid red;width:100px;height:100px;"/>'),simulateEvent("dblclick",0,e.editor.querySelector("img"));var t=e.ownerDocument.querySelector(".jodit.jodit_dialog_box.active");expect(t.style.display).to.be.not.equal("none"),expect(t.querySelectorAll("input.margins[disabled]").length).to.equal(3)}),it("Should enable margin inputs for left, bottom, right if element has not equals margins(margin:10px 5px;)",function(){var e=new Jodit(appendTestArea(),{observer:{timeout:0},image:{openOnDblClick:!0}});e.setEditorValue('<img src="https://xdsoft.net/jodit/build/images/artio.jpg" style="margin:10px 5px;border:1px solid red;width:100px;height:100px;"/>'),simulateEvent("dblclick",0,e.editor.querySelector("img"));var t=e.ownerDocument.querySelector(".jodit.jodit_dialog_box.active");expect(t.style.display).to.be.not.equal("none"),expect(t.querySelectorAll("input.margins[disabled]").length).to.equal(0)})})}),afterEach(removeStuff)});